generated.css:
@font-face {
	font-family: Gnuolane;
	src: url("decb60b56e098bd932222dc4759732aa.ttf");
}

body {
	background-color: #f4f7f8;
	font-family: 'Source Sans Pro', sans-serif;
	font-size: 15px;
}

h1, h2, h3, h4 {
	font-family: Gnuolane, sans-serif;
}

a {
	color: #37BAE6;
}

.wrapper {
	height: 100vh;
	display: flex;
	align-items: stretch;
}

.drync-grid {
	width: 100%;
	display: grid;
	grid-template-columns: [nav] 102px [content] 1fr;
	grid-template-rows: 51px auto;
	grid-template-areas:
		"header header"
		"nav main";
}

header {
	grid-area: header;
}

.header-bg {
	background-color: #fff;
	width: 100%;
	height: 50px;
	border-bottom: 1px solid #e7e7e7;
	display: grid;
	grid-template-columns: [logo] 1fr [store-select] 195px [avatar] 55px;
	grid-template-rows: 51px;
	grid-template-areas:
		"logo store-select avatar";
}

.header-bg h1 {
	margin: 0 0 0 30px;
	padding: 0;
	width: 148px;
	height: 35px;
	position: relative;
	grid-area: logo;
	align-self:center;
	display:flex;
	align-items:stretch;
}

.header-bg h1 a {
	background-image: url('de5227c4ba8aceb3db50246f4ec5923a.svg');
	background-size: 148px 35px;
	width: 148px;
	height: 35px;
}

.header-bg h1 a span {
	display: none;
}

.store-selection {
	grid-area: store-select;
	align-self:center;
}

.store-selection select {
	width:180px;
	font-size:15px;
}

.user-avatar {
	grid-area: avatar;
	display:flex;
	justify-content:center;
	align-items:center;
}

.user-avatar a {
	background-image: url("b2e9e6ea3641c37c766db8e1af77d200.svg");
	background-size: 35px 35px;
	width:35px;
	height:35px;
}

nav {
	background-color: #1F2022;
	grid-area: nav;
}

.nav-wrapper {
	padding-top:30px;
	display: grid;
	grid-template-columns: 102px;
	grid-template-rows: 70px 82px repeat(5, 70px);
	grid-template-areas:
		"dashboard"
		"order-management"
		"reporting"
		"marketing"
		"promotions"
		"store"
		"help";
}

.nav-wrapper div {
	background-size: 25px 25px;
	background-repeat: no-repeat;
	background-position: center 10px;
	display:flex;
	justify-content:center;
	align-items:stretch;
	transition: 100ms;
  transition-timing-function: ease-in-out;
  -webkit-transition: 200ms;
  -webkit-transition-timing-function: ease-in-out;

}

.nav-wrapper div:hover {
	background-color: #666;
}

.nav-wrapper div.active {
	background-color: #fff;
}

.nav-wrapper .active a {
	color: #27292B;
}

.nav-wrapper div a {
	padding-top:38px;
	width: 102px;
	color: #fff;
	font-size:13px;
	text-decoration: none;
	text-align: center;
}

.nav-item-01 {
	background-image: url("19b8eaa7036675dbcd1611be0574cc09.svg");
	grid-area: dashboard;
}

.nav-item-02 {
	background-image: url("0b9dad0c50186f5a42f38e25ae40eb69.svg");
	grid-area: order-management;
}

.nav-item-03 {
	background-image: url("67533dcaeec96cd9e8c1ee9442c32171.svg");
	grid-area: reporting;
}

.nav-item-04 {
	background-image: url("93648440af337d9db08556f1d09ba447.svg");
	grid-area: marketing;
}

.nav-item-05 {
	background-image: url("c42049846c6efa23733bccc472030307.svg");
	background-position: center 8px !important;
	background-size: 33px 33px !important;
	grid-area: promotions;
}

.nav-item-05.active {
	background-image: url("1f1801ac70ca4690e59ca1ed1521c360.svg");
}

.nav-item-06 {
	background-image: url("d16f8a22ddfd1d75885990613defba2b.svg");
	grid-area: store;
}

.nav-item-07 {
	background-image: url("052ddbb87b78cb806f7732cff9af36b1.svg");
	grid-area: help;
}

main {
	grid-area: main;
	overflow: auto;
}

.content-wrapper {
	padding-left:24px;
	width:902px;
}

.content-wrapper h2 {
	margin: 0;
	padding: 20px 0;
	font-size: 32px;
}

.content-bg {
	background-color: #fff;
  margin-bottom: 12px;
  padding: 13px 12px;
  border-right: 1px solid #d7d8d8;
  border-bottom: 1px solid #d7d8d8;
}

.content-heading {
	display: flex;
	align-items: center;
}

.content-heading h2 {
	margin-left: 12px;
	flex: 1 0 auto;
}

.content-heading .add-new {
	margin-right: 12px;
	flex: 0 0 auto;
}

.promotions-grid {
	display: grid;
	grid-template-columns: [Name] 217px [Code] 102px [Discount] 68px [Period] auto [Type] 87px [Minimum] 72px [Status] 100px [Click] 40px;
	grid-template-rows: 40px ;
	grid-auto-rows: 55px;
}

.promotions-grid div {
	background-color: #fff;
}

.promo-heading {
  color: #9EA2A3;
  font-size: 0.9333rem;
  display: flex;
  align-items: center;
  align-content: stretch;
}

.promo-heading.status {
	grid-column-start: 7;
	grid-column-end: 9;
}

.promo-data {
	border-bottom: 1px solid #EEF0F1;
	display: flex;
	align-items: center;
	align-content: stretch;
}

.promo-heading.name, .promo-data.name {
	padding-left: 12px;
}

.promo-data.disc, .promo-data.min, .promo-heading.status, .promo-data.status {
	padding-right: 12px;
	justify-content: flex-end;
}

.promo-heading.period, .promo-data.period {
	justify-content: center;
}

.promo-data img {
	margin-top: 4px;
}

.promo-data a {
	font-size: 1.333rem;
	text-decoration: none;
}
